from django.shortcuts import render,redirectfrom django.contrib import messagesfrom django.contrib.auth.models import User,auth# Create your views here.def login_user(request):    # SAVE_DIR = image_request(request)    if not request.user.is_authenticated:        if request.method == 'POST':            username = request.POST['username']            password = request.POST['password']            print(username,password)            user = auth.authenticate(username=username, password =password)            if user is not None:                auth.login(request, user)                return redirect('dashboard:validate')            else:                messages.info(request, 'Invalid Username or Password')                return redirect('authentication:login_user')        else:            return render(request, 'authentication/login.html')    else:        return render(request, 'authentication/login.html')def logout_user(request):    auth.logout(request)    return redirect('/login')def registeration(request):    if request.method == 'POST':        first_name = request.POST['first_name']        last_name = request.POST['last_name']        username = request.POST['username']        email = request.POST['email']        password = request.POST['password']        confirm_password = request.POST['confirm_password']        if password == confirm_password:            if User.objects.filter(username=username).exists():                messages.info(request, 'Username is already taken')                return redirect('authentication:registeration')            elif User.objects.filter(email=email).exists():                messages.info(request, 'Email is already taken')                return redirect('authentication:registeration')            else:                user = User.objects.create_user(username=username, password=password,                                                email=email, first_name=first_name, last_name=last_name)                user.save()                return redirect('authentication:login_user')        else:            messages.info(request, 'Both passwords are not matching')            return redirect('authentication:registeration')    else:        return render(request, 'authentication/register.html')    # return render(request,'authenti-----    # cation/register.html')